        // Verify realtime-platform existence in the cluster
        exists, err := r.verifyRealtimePlatformExists(ctx, *app.Spec.Realtime, app.Namespace)
        if err != nil {
            return ctrl.Result{}, err
        }

        if !exists {
            // Log error and update status
