            meta.SetStatusCondition(&app.Status.Conditions, metav1.Condition{
                Type:    "RealtimePlatformReady",
                Status:  metav1.ConditionFalse,
                Reason:  "RealtimePlatformNotFound",
                Message: fmt.Sprintf("Realtime platform %s not found in namespace %s", *app.Spec.Realtime, app.Namespace),
            })
            return ctrl.Result{}, err
        }

        // Call service enhancement orchestration
        if err := r.orchestrateServiceEnhancement(ctx, app); err != nil {
            return ctrl.Result{}, err
        }
    }

    // Continue with existing reconciliation
}
