// Manager handles smart service management operations
type Manager struct {
    client client.Client
    scheme *runtime.Scheme
}

// NewManager creates a new smart service manager
func NewManager(client client.Client, scheme *runtime.Scheme) *Manager {
    return &Manager{
        client: client,
        scheme: scheme,
    }
}

// EnhanceOrCreateService enhances an existing service or creates a new one
func (m *Manager) EnhanceOrCreateService(ctx context.Context, options EnhancementOptions) error {
    // Check if service exists
    exists, err := m.serviceExists(ctx, options.ApplicationName, options.Namespace)
    if err != nil {
        return err
    }

    if exists {
        return m.enhanceExistingService(ctx, options)
    }

    return m.createNewService(ctx, options)
}

