   ```go
   // Add to the reconciliation logic
   func (r *ApplicationReconciler) ensureSecretDiscoverability(ctx context.Context, app *platformv1alpha1.Application) error {
       if app.Spec.Realtime == nil {
           return nil // No realtime integration, nothing to do
       }

       // Find all secrets related to the realtime platform
       realtimeSecrets, err := r.secretController.discoverRealtimePlatformSecrets(ctx, *app.Spec.Realtime)
       if err != nil {
           return err
       }

       // Add labels to make secrets discoverable by the application
       for i := range realtimeSecrets {
           secret := &realtimeSecrets[i]
           if secret.Labels == nil {
               secret.Labels = make(map[string]string)
           }
           secret.Labels["app.kubernetes.io/managed-by"] = app.Name
           secret.Labels["app.kubernetes.io/discoverable"] = "true"

           if err := r.Update(ctx, secret); err != nil {
               return fmt.Errorf("failed to update secret %s: %w", secret.Name, err)
           }
       }

       return nil
   }
   ```

4. Create a new SecretInjector controller that watches for deployments with the realtime integration annotation and injects the appropriate secrets:
   ```go
   // In a new file: controllers/secret_injector_controller.go
   type SecretInjectorController struct {
       client.Client
       Log logr.Logger
       Scheme *runtime.Scheme
   }

   // +kubebuilder:rbac:groups=apps,resources=deployments,verbs=get;list;watch;update;patch
   // +kubebuilder:rbac:groups=core,resources=secrets,verbs=get;list;watch

   func (r *SecretInjectorController) Reconcile(ctx context.Context, req ctrl.Request) (ctrl.Result, error) {
       log := r.Log.WithValues("deployment", req.NamespacedName)

       // Get the deployment
       deployment := &appsv1.Deployment{}
       if err := r.Get(ctx, req.NamespacedName, deployment); err != nil {
           return ctrl.Result{}, client.IgnoreNotFound(err)
       }

       // Check if this deployment has realtime integration
       realtimeName, ok := deployment.Annotations["realtime.platform.example.org/integration"]
       if !ok {
           return ctrl.Result{}, nil // No realtime integration
       }

       // Discover realtime platform secrets
       secrets, err := r.discoverRealtimePlatformSecrets(ctx, realtimeName)
       if err != nil {
           log.Error(err, "Failed to discover realtime platform secrets")
           return ctrl.Result{}, err
       }

       // Inject secrets into the deployment
       if err := r.injectSecretsToWebservice(ctx, deployment, secrets); err != nil {
           log.Error(err, "Failed to inject secrets into webservice")
           return ctrl.Result{}, err
       }

       log.Info("Successfully injected realtime platform secrets", "count", len(secrets))
       return ctrl.Result{}, nil
   }
   ```

